'use client';

import { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  MicrophoneIcon, 
  SpeakerWaveIcon, 
  XMarkIcon,
  SparklesIcon 
} from '@heroicons/react/24/outline';

interface VoiceAIAssistantProps {
  isOpen: boolean;
  onClose: () => void;
  onConnect: () => void;
  isConnected: boolean;
  isListening: boolean;
  isSpeaking: boolean;
}

export default function VoiceAIAssistant({
  isOpen,
  onClose,
  onConnect,
  isConnected,
  isListening,
  isSpeaking
}: VoiceAIAssistantProps) {
  const [pulseAnimation, setPulseAnimation] = useState(false);

  useEffect(() => {
    if (isListening || isSpeaking) {
      setPulseAnimation(true);
    } else {
      setPulseAnimation(false);
    }
  }, [isListening, isSpeaking]);

  return (
    <AnimatePresence>
      {isOpen && (
        <motion.div
          initial={{ opacity: 0, scale: 0.8, y: 20 }}
          animate={{ opacity: 1, scale: 1, y: 0 }}
          exit={{ opacity: 0, scale: 0.8, y: 20 }}
          transition={{ duration: 0.3, ease: "easeOut" }}
          className="fixed bottom-24 right-6 z-50 bg-background/95 backdrop-blur-xl border border-white/20 rounded-2xl shadow-2xl p-6 w-80 max-w-[calc(100vw-2rem)]"
        >
          {/* Header */}
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center space-x-2">
              <motion.div
                animate={{ rotate: pulseAnimation ? 360 : 0 }}
                transition={{ duration: 2, repeat: pulseAnimation ? Infinity : 0, ease: "linear" }}
                className="w-8 h-8 rounded-full bg-gradient-to-r from-primary to-accent flex items-center justify-center"
              >
                <SparklesIcon className="w-4 h-4 text-white" />
              </motion.div>
              <h3 className="text-lg font-semibold gradient-text">Voice AI Assistant</h3>
            </div>
            <button
              onClick={onClose}
              className="p-2 hover:bg-white/10 rounded-full transition-colors"
            >
              <XMarkIcon className="w-5 h-5 text-foreground" />
            </button>
          </div>

          {/* Connection Status */}
          <div className="mb-4">
            <div className="flex items-center space-x-2 mb-2">
              <div className={`w-2 h-2 rounded-full ${isConnected ? 'bg-green-500' : 'bg-red-500'}`} />
              <span className="text-sm text-muted-foreground">
                {isConnected ? 'Connected' : 'Disconnected'}
              </span>
            </div>
            {isConnected && (
              <div className="flex items-center space-x-2">
                <div className={`w-2 h-2 rounded-full ${isListening ? 'bg-blue-500' : 'bg-gray-500'}`} />
                <span className="text-sm text-muted-foreground">
                  {isListening ? 'Listening...' : 'Ready'}
                </span>
              </div>
            )}
          </div>

          {/* Voice Visualizer */}
          <div className="mb-6 flex items-center justify-center h-32">
            <motion.div
              className="relative w-24 h-24 rounded-full bg-gradient-to-r from-primary to-accent flex items-center justify-center"
              animate={{
                scale: pulseAnimation ? [1, 1.1, 1] : 1,
                opacity: pulseAnimation ? [0.8, 1, 0.8] : 0.9
              }}
              transition={{
                duration: 1.5,
                repeat: pulseAnimation ? Infinity : 0,
                ease: "easeInOut"
              }}
            >
              {/* Outer glow rings */}
              <motion.div
                className="absolute inset-0 rounded-full border-2 border-primary/30"
                animate={{
                  scale: pulseAnimation ? [1, 1.5, 1] : 1,
                  opacity: pulseAnimation ? [0.5, 0, 0.5] : 0
                }}
                transition={{
                  duration: 2,
                  repeat: pulseAnimation ? Infinity : 0,
                  ease: "easeOut"
                }}
              />
              <motion.div
                className="absolute inset-0 rounded-full border-2 border-accent/30"
                animate={{
                  scale: pulseAnimation ? [1, 1.8, 1] : 1,
                  opacity: pulseAnimation ? [0.3, 0, 0.3] : 0
                }}
                transition={{
                  duration: 2,
                  repeat: pulseAnimation ? Infinity : 0,
                  ease: "easeOut",
                  delay: 0.5
                }}
              />
              
              {/* Center icon */}
              <motion.div
                animate={{ rotate: isSpeaking ? [0, 5, -5, 0] : 0 }}
                transition={{ duration: 0.5, repeat: isSpeaking ? Infinity : 0 }}
              >
                {isSpeaking ? (
                  <SpeakerWaveIcon className="w-8 h-8 text-white" />
                ) : (
                  <MicrophoneIcon className="w-8 h-8 text-white" />
                )}
              </motion.div>
            </motion.div>
          </div>

          {/* Controls */}
          <div className="space-y-3">
            {!isConnected ? (
              <button
                onClick={onConnect}
                className="w-full btn-primary flex items-center justify-center space-x-2 glow"
              >
                <MicrophoneIcon className="w-5 h-5" />
                <span>Start Voice Chat</span>
              </button>
            ) : (
              <div className="text-center space-y-2">
                <p className="text-sm text-muted-foreground">
                  {isListening ? 'I\'m listening... speak away!' : 
                   isSpeaking ? 'I\'m speaking...' : 
                   'Connected and ready to help!'}
                </p>
                <button
                  onClick={onClose}
                  className="btn-outline text-sm"
                >
                  End Chat
                </button>
              </div>
            )}
          </div>

          {/* Instructions */}
          <div className="mt-4 p-3 bg-white/5 rounded-lg border border-white/10">
            <p className="text-xs text-muted-foreground">
              ð¡ Ask me about the portfolio projects, skills, or anything else you'd like to know!
            </p>
          </div>
        </motion.div>
      )}
    </AnimatePresence>
  );
}

