'use client';

import { motion } from 'framer-motion';
import { MicrophoneIcon, SparklesIcon } from '@heroicons/react/24/outline';

interface VoiceAIFabProps {
  onClick: () => void;
  isActive?: boolean;
}

export default function VoiceAIFab({ onClick, isActive = false }: VoiceAIFabProps) {
  return (
    <motion.button
      onClick={onClick}
      className="fixed bottom-6 right-6 z-40 w-14 h-14 rounded-full bg-gradient-to-r from-primary to-accent shadow-lg hover:shadow-xl transition-all duration-300 flex items-center justify-center group glow"
      whileHover={{ scale: 1.1 }}
      whileTap={{ scale: 0.95 }}
      initial={{ opacity: 0, scale: 0 }}
      animate={{ opacity: 1, scale: 1 }}
      transition={{ 
        type: "spring", 
        stiffness: 260, 
        damping: 20,
        delay: 1.5 // Delay to appear after page loads
      }}
    >
      {/* Floating animation background */}
      <motion.div
        className="absolute inset-0 rounded-full bg-gradient-to-r from-primary to-accent"
        animate={{
          scale: [1, 1.2, 1],
          opacity: [0.8, 0.4, 0.8],
        }}
        transition={{
          duration: 2,
          repeat: Infinity,
          ease: "easeInOut"
        }}
      />
      
      {/* Sparkle animation for active state */}
      {isActive && (
        <motion.div
          className="absolute inset-0 rounded-full"
          initial={{ scale: 0, rotate: 0 }}
          animate={{ 
            scale: [0, 1.5, 0],
            rotate: [0, 180, 360],
            opacity: [0, 0.6, 0]
          }}
          transition={{
            duration: 1.5,
            repeat: Infinity,
            ease: "easeOut"
          }}
        >
          <SparklesIcon className="w-6 h-6 text-white absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2" />
        </motion.div>
      )}
      
      {/* Main icon */}
      <motion.div
        className="relative z-10"
        animate={{ 
          rotate: isActive ? [0, 10, -10, 0] : 0,
          scale: isActive ? [1, 1.1, 1] : 1
        }}
        transition={{
          duration: 0.5,
          repeat: isActive ? Infinity : 0,
          ease: "easeInOut"
        }}
      >
        <MicrophoneIcon className="w-6 h-6 text-white" />
      </motion.div>
      
      {/* Hover tooltip */}
      <motion.div
        className="absolute -top-12 left-1/2 transform -translate-x-1/2 bg-background/95 backdrop-blur-sm border border-white/20 rounded-lg px-3 py-1 text-xs font-medium text-foreground whitespace-nowrap opacity-0 group-hover:opacity-100 transition-opacity duration-200"
        initial={{ y: 10, opacity: 0 }}
        whileHover={{ y: 0, opacity: 1 }}
      >
        Voice AI Assistant
        <div className="absolute top-full left-1/2 transform -translate-x-1/2 w-2 h-2 bg-background/95 border-r border-b border-white/20 rotate-45" />
      </motion.div>
    </motion.button>
  );
}

